                _arguments=$(echo "$_command_arguments" | sed -n -E 's/^([^\(]+)\(([^\)]*)\)$/\2/p')
                if command -v "$_command" > /dev/null;then
                    if [ -n "$argument_placeholders" ];then
                        while read line; do
                            find=$(echo ${line} | cut -d: -f1 | xargs)
                            replace=$(echo ${line} | cut -d: -f2 | xargs)
                            label="${label/"$find"/"$replace"}"
